ping.run:
async (client, message, args) => {
    const msg = await message.channel.send(`Pinging...`);

    msg.edit(`Pong! Latency is ${Math.floor(msg.createdTimestamp - message.createdTimestamp)}ms. API Latency is ${Math.round(client.ws.ping)}ms`);
}

say.run:
async (client, message, args) => {
    if (!args[0]) return message.channel.send(`Please provide a text`);

    message.channel.send(args.join(` `));
}

help.run:
async (client, message, args) => {
    if (args[0]) {
        return getCMD(client, message, args[0]);
    } else {
        return getAll(client, message);
    }
}

control.run:
async (client, message, args) => {
    if (!args[0]) return message.channel.send(`Please provide a command`);

    if (args[0] === `reload`) {
        client.commands.forEach(async cmd => {
            await client.unloadCommand(cmd.help.name);
            client.loadCommand(cmd.help.name);
        });

        return message.channel.send(`Reloaded all commands!`);
    }
}
