ping.run:
async (client, message, args) => {
    const msg = await message.channel.send(`Pinging...`);

    msg.edit(`Pong! Latency is ${Math.floor(msg.createdTimestamp - message.createdTimestamp)}ms. API Latency is ${Math.round(client.ws.ping)}ms`);
}

say.run:
async (client, message, args) => {
    if (!args[0]) return message.channel.send(`Please provide a text`);

    message.channel.send(args.join(` `));
}

help.run:
async (client, message, args) => {
    if (args[0]) {
        return getCMD(client, message, args[0]);
    } else {
        return getAll(client, message);
    }
}

add.run:
async (client, message, args) => {
    if (message.author.id !== 'YOUR ID') return;

    const name = args[0];
    const description = args[1];
    const usage = args[2];
    const aliases = args[3].split(',');
    const cooldown = args[4];
    const category = args[5];
    const permissions = args[6].split(',');
    const run = args[7];

    client.commands.set(name, {
        name: name,
        description: description,
        usage: usage,
        aliases: aliases,
        cooldown: cooldown,
        category: category,
        permissions: permissions,
        run: run
    });

    message.channel.send(`Command added!`);
}
